// Enhanced continuation logic for long-form story generation
const raw = $('Set default inputs').first().json;
raw.text = $input.first().json.text;

// Get target word count (default to 20000 if not provided)
const targetWordCount = raw.word_count || raw.targetWordCount || 20000;

// Initialize state variables on first run
if (typeof raw.cache === 'undefined') {
  raw.cache = '';           
  raw.continue = '';        
  raw.segmentCount = 0;     
  raw.totalWords = 0;       
  raw.contextSummary = '';  
}

// Handle the incoming text from LLM
let currentSegment = '';
if (typeof raw.text !== 'undefined') {
  currentSegment = raw.text;
} else if (typeof raw.story !== 'undefined') {
  currentSegment = raw.story;
} else if (typeof raw.response !== 'undefined') {
  currentSegment = raw.response;
}

// Check for completion marker
const isComplete = currentSegment.includes('-- THE END --');

// IMPORTANT: Only concatenate if this is a NEW segment
// Avoid double-concatenation if cache already contains this segment
if (raw.segmentCount === 0 || !raw.cache.includes(currentSegment.substring(0, 100))) {
  // Add spacing between segments if cache exists
  if (raw.cache.length > 0 && currentSegment.length > 0) {
    raw.cache += '\n\n' + currentSegment;
  } else {
    raw.cache += currentSegment;
  }
  raw.segmentCount += 1;
}

// Estimate word count from the full cache
const wordCount = raw.cache.trim().split(/\s+/).filter(w => w.length > 0).length;
raw.totalWords = wordCount;

// Calculate words remaining to target
const wordsRemaining = Math.max(0, targetWordCount - wordCount);
const progressPercent = Math.min(100, Math.round((wordCount / targetWordCount) * 100));

// Generate context summary every 3 segments to manage context window
if (raw.segmentCount % 3 === 0 && !isComplete) {
  raw.contextSummary = `[Progress: ~${wordCount} words across ${raw.segmentCount} segments | Target: ${targetWordCount} words | Remaining: ~${wordsRemaining} words]`;
}

if (!isComplete && wordsRemaining > 0) {
  // Extract continuation point from current segment
  const lastSegment = currentSegment.slice(-500);
  const sentenceEndings = /[.!?]["']?\s/g;
  const matches = [...lastSegment.matchAll(sentenceEndings)];

  let continuationPoint;
  if (matches.length >= 2) {
    const secondLastSentence = matches[matches.length - 2].index;
    continuationPoint = lastSegment.slice(secondLastSentence).trim();
  } else {
    continuationPoint = lastSegment.slice(-200).trim();
  }

  // Calculate suggested words for next segment
  const suggestedSegmentWords = Math.min(4000, Math.max(2000, wordsRemaining));

  // Build continuation prompt for USER message
  raw.continue = `[CONTINUE PROMPT]

Story Progress:
- Segments completed: ${raw.segmentCount}
- Current word count: ${wordCount} words
- Target word count: ${targetWordCount} words
- Words remaining: ~${wordsRemaining} words
- Suggested for this segment: ~${suggestedSegmentWords} words

Continue the story seamlessly from this exact point:
"...${continuationPoint}"

Instructions: Pick up exactly where the narrative left off. Do not repeat or summarize what came before. Maintain the same voice, scene, and momentum. Write approximately ${suggestedSegmentWords} words${wordsRemaining <= 4000 ? ' and bring the story to a satisfying conclusion with "-- THE END --"' : ' before the next natural break point'}.`;

} else if (isComplete) {
  // Story has ended
  raw.continue = '';
} else {
  // Target reached but no end marker - prompt for conclusion
  raw.continue = `[CONTINUE PROMPT]

Story Progress:
- Target word count reached: ${wordCount} / ${targetWordCount} words
- Segments completed: ${raw.segmentCount}

Continue from this point:
"...${currentSegment.slice(-200).trim()}"

Instructions: You have reached the target word count. Please bring the story to a natural and satisfying conclusion within the next 1,000-2,000 words. Resolve all major plot threads and end with "-- THE END --".`;
}

// Store current segment for reference
raw.story = currentSegment;

return {
  isComplete: isComplete,
  story: currentSegment,        // Current segment only
  cache: raw.cache,              // Full accumulated story
  continue: raw.continue,        // Next prompt (empty if complete)
  segmentCount: raw.segmentCount,
  totalWords: raw.totalWords,
  targetWordCount: targetWordCount,
  wordsRemaining: wordsRemaining,
  contextSummary: raw.contextSummary,
  progressPercent: progressPercent,
  shouldContinue: !isComplete && wordsRemaining > 0
};
