const urls = $('Start').first().json.image_urls;
const texts = $('Start').first().json.image_texts;
let slides = [];

if (urls.length !== texts.length) {
  throw new Error('Number of URL and Text elements must match!')
}

for (let i = 0; i < urls.length; i++) {
  slides.push({"image_url": urls[i], "text": texts[i]});
}

return {"slides": slides}

// Get node-specific static data (persists across workflow runs)
const data = $getWorkflowStaticData('node');

// Initialize attempt if it doesn't exist
if (typeof data.attempt === 'undefined' || data.job_id !== $input.first().json.job_id) {
  data.attempt = 0;
  data.job_id = $input.first().json.job_id
}

// Increment attempt
data.attempt += 1;

// Return the current attempt count
return [
  { json: 
    { attempts: data.attempt, 
      status: $('Check Slideshow Status').first().json.status, 
      result: $("Check Slideshow Status").first().json.result
    } 
  }
];
